Schema:
{
  "typeName": "AWS::Cognito::IdentityPool",
  "description": "Resource Type definition for AWS::Cognito::IdentityPool",
  "additionalProperties": false,
  "properties": {
    "PushSync": {
      "$ref": "#/definitions/PushSync"
    },
    "CognitoIdentityProviders": {
      "type": "array",
      "uniqueItems": false,
      "items": {
        "$ref": "#/definitions/CognitoIdentityProvider"
      }
    },
    "DeveloperProviderName": {
      "type": "string"
    },
    "CognitoStreams": {
      "$ref": "#/definitions/CognitoStreams"
    },
    "SupportedLoginProviders": {
      "type": "object"
    },
    "Name": {
      "type": "string"
    },
    "CognitoEvents": {
      "type": "object"
    },
    "Id": {
      "type": "string"
    },
    "IdentityPoolName": {
      "type": "string"
    },
    "AllowUnauthenticatedIdentities": {
      "type": "boolean"
    },
    "SamlProviderARNs": {
      "type": "array",
      "uniqueItems": false,
      "items": {
        "type": "string"
      }
    },
    "OpenIdConnectProviderARNs": {
      "type": "array",
      "uniqueItems": false,
      "items": {
        "type": "string"
      }
    },
    "AllowClassicFlow": {
      "type": "boolean"
    }
  },
  "definitions": {
    "PushSync": {
      "type": "object",
      "additionalProperties": false,
      "properties": {
        "ApplicationArns": {
          "type": "array",
