        },
        "ClientId": {
          "type": "string"
        }
      }
    },
    "CognitoStreams": {
      "type": "object",
      "additionalProperties": false,
      "properties": {
        "StreamingStatus": {
          "type": "string"
        },
        "StreamName": {
          "type": "string"
        },
        "RoleArn": {
          "type": "string"
        }
      }
    }
  },
  "required": [
    "AllowUnauthenticatedIdentities"
  ],
  "primaryIdentifier": [
    "/properties/Id"
  ],
  "readOnlyProperties": [
    "/properties/Id",
    "/properties/Name"
