Schema:
{
  "typeName": "AWS::DataBrew::Schedule",
  "description": "Resource schema for AWS::DataBrew::Schedule.",
  "sourceUrl": "https://github.com/aws-cloudformation/aws-cloudformation-resource-providers-databrew.git",
  "taggable": true,
  "properties": {
    "JobNames": {
      "type": "array",
      "insertionOrder": true,
      "uniqueItems": true,
      "items": {
        "$ref": "#/definitions/JobName"
      }
    },
    "CronExpression": {
      "description": "Schedule cron",
      "type": "string",
      "minLength": 1,
      "maxLength": 512
    },
    "Name": {
      "description": "Schedule Name",
      "type": "string",
      "minLength": 1,
      "maxLength": 255
    },
    "Tags": {
      "type": "array",
      "insertionOrder": false,
      "uniqueItems": false,
      "items": {
        "$ref": "#/definitions/Tag"
      }
    }
  },
  "definitions": {
    "JobName": {
      "description": "Job name",
      "type": "string",
      "minLength": 1,
      "maxLength": 255
    },
    "Tag": {
      "description": "A key-value pair to associate with a resource.",
      "type": "object",
      "properties": {
        "Key": {
          "type": "string",
          "minLength": 1,
          "maxLength": 128
        },
        "Value": {
          "type": "string",
          "minLength": 0,
          "maxLength": 256
        }
      },
      "additionalProperties": false,
      "required": ["Value", "Key"]
    }
  },
  "additionalProperties": false,
  "required": ["Name", "CronExpression"],
  "primaryIdentifier": ["/properties/Name"],
  "createOnlyProperties": ["/properties/Name", "/properties/Tags"],
  "handlers": {
    "create": {
      "permissions": [
        "databrew:CreateSchedule",
        "databrew:TagResource",
        "databrew:UntagResource",
        "iam:PassRole"
      ]
    },
    "read": {
      "permissions": [
        "databrew:DescribeSchedule",
        "databrew:ListTagsForResource",
        "iam:ListRoles"
      ]
    },
    "update": {
      "permissions": ["databrew:UpdateSchedule"]
    },
    "delete": {
      "permissions": ["databrew:DeleteSchedule"]
    },
    "list": {
      "permissions": [
        "databrew:ListSchedules",
        "databrew:ListTagsForResource",
        "iam:ListRoles"
      ]
    }
  }
}
