Schema:
{
  "typeName": "AWS::EC2::VPNGateway",
  "description": "Schema for EC2 VPN Gateway",
  "sourceUrl": "https://github.com/aws-cloudformation/aws-cloudformation-rpdk.git",
  "definitions": {
    "Tag": {
      "type": "object",
      "additionalProperties": false,
      "properties": {
        "Key": {
          "type": "string"
        },
        "Value": {
          "type": "string"
        }
      },
      "required": [
        "Value",
        "Key"
      ]
    }
  },
  "properties": {
    "VPNGatewayId": {
      "description": "VPN Gateway ID generated by service",
      "type": "string"
    },
    "AmazonSideAsn": {
      "description": "The private Autonomous System Number (ASN) for the Amazon side of a BGP session.",
      "type": "integer",
      "format": "int64"
    },
    "Tags": {
      "description": "Any tags assigned to the virtual private gateway.",
      "type": "array",
      "insertionOrder": false,
      "uniqueItems": false,
      "items": {
        "$ref": "#/definitions/Tag"
      }
    },
    "Type": {
      "description": "The type of VPN connection the virtual private gateway supports.",
      "type": "string"
    }
  },
  "additionalProperties": false,
  "required": [
    "Type"
  ],
  "tagging": {
    "taggable": true,
    "tagOnCreate": true,
    "tagUpdatable": true,
    "cloudFormationSystemTags": true,
    "tagProperty": "/properties/Tags"
  },
  "createOnlyProperties": [
    "/properties/AmazonSideAsn",
    "/properties/Type"
  ],
  "readOnlyProperties": [
    "/properties/VPNGatewayId"
  ],
  "primaryIdentifier": [
    "/properties/VPNGatewayId"
  ],
  "handlers": {
    "create": {
      "permissions": [
        "ec2:CreateVpnGateway",
        "ec2:DescribeVpnGateways"
      ]
    },
    "read": {
      "permissions": [
        "ec2:DescribeVpnGateways"
      ]
    },
    "update": {
      "permissions": [
        "ec2:CreateTags",
        "ec2:DeleteTags"
      ]
    },
    "delete": {
      "permissions": [
        "ec2:DeleteVpnGateway"
      ]
    },
    "list": {
      "permissions": [
        "ec2:DescribeVpnGateways"
      ]
    }
  }
}
