Schema:
{
  "typeName": "AWS::Kendra::DataSource",
  "description": "Kendra DataSource",
  "sourceUrl": "https://docs.aws.amazon.com/kendra/latest/dg/hiw-data-source.html",
  "definitions": {
    "IndexId": {
      "description": "ID of Index",
      "type": "string",
      "minLength": 36,
      "maxLength": 36
    },
    "Tag": {
      "description": "A label for tagging Kendra resources",
      "type": "object",
      "properties": {
        "Key": {
          "description": "A string used to identify this tag",
          "type": "string",
          "minLength": 1,
          "maxLength": 128
        },
        "Value": {
          "description": "A string containing the value for the tag",
          "type": "string",
          "minLength": 0,
          "maxLength": 256
        }
      },
      "required": [
        "Key",
        "Value"
      ],
      "additionalProperties": false
    },
    "TagList": {
      "description": "List of tags",
      "type": "array",
      "maxItems": 200,
      "items": {
        "$ref": "#/definitions/Tag"
      }
    },
    "DataSourceInclusionsExclusionsStrings": {
      "type": "array",
      "maxItems": 100,
      "items": {
        "type": "string",
        "minLength": 1,
        "maxLength": 50
      }
    },
    "S3Path": {
      "type": "object",
      "properties": {
        "Bucket": {
          "$ref": "#/definitions/S3BucketName"
        },
        "Key": {
          "$ref": "#/definitions/S3ObjectKey"
        }
      },
      "additionalProperties": false,
      "required": [
        "Bucket",
        "Key"
      ]
    },
    "S3BucketName": {
      "type": "string",
      "minLength": 3,
      "maxLength": 63,
      "pattern": "[a-z0-9][\\.\\-a-z0-9]{1,61}[a-z0-9]"
    },
    "S3ObjectKey": {
      "type": "string",
      "minLength": 1,
      "maxLength": 1024
    },
    "DocumentsMetadataConfiguration": {
      "type": "object",
      "properties": {
        "S3Prefix": {
          "$ref": "#/definitions/S3ObjectKey"
        }
      },
      "additionalProperties": false
    },
    "AccessControlListConfiguration": {
      "type": "object",
      "properties": {
        "KeyPath": {
          "$ref": "#/definitions/S3ObjectKey"
        }
      },
      "additionalProperties": false
    },
    "S3DataSourceConfiguration": {
      "description": "S3 data source configuration",
      "type": "object",
      "properties": {
        "BucketName": {
          "$ref": "#/definitions/S3BucketName"
        },
        "InclusionPrefixes": {
          "$ref": "#/definitions/DataSourceInclusionsExclusionsStrings"
        },
        "InclusionPatterns": {
          "$ref": "#/definitions/DataSourceInclusionsExclusionsStrings"
        },
        "ExclusionPatterns": {
          "$ref": "#/definitions/DataSourceInclusionsExclusionsStrings"
        },
        "DocumentsMetadataConfiguration": {
          "$ref": "#/definitions/DocumentsMetadataConfiguration"
        },
        "AccessControlListConfiguration": {
          "$ref": "#/definitions/AccessControlListConfiguration"
        }
      },
      "additionalProperties": false,
      "required": [
        "BucketName"
      ]
    },
    "Url": {
      "type": "string",
      "minLength": 1,
      "maxLength": 2048,
      "pattern": "^(https?|ftp|file)://([^\\s]*)"
    },
    "SecretArn": {
      "type": "string",
      "minLength": 1,
      "maxLength": 1284,
      "pattern": "arn:[a-z0-9-\\.]{1,63}:[a-z0-9-\\.]{0,63}:[a-z0-9-\\.]{0,63}:[a-z0-9-\\.]{0,63}:[^/].{0,1023}"
    },
    "DataSourceVpcConfiguration": {
      "type": "object",
      "properties": {
        "SubnetIds": {
          "type": "array",
          "maxItems": 6,
          "items": {
            "type": "string",
            "minLength": 1,
            "maxLength": 200,
            "pattern": "[\\-0-9a-zA-Z]+"
          }
        },
        "SecurityGroupIds": {
          "type": "array",
          "maxItems": 10,
          "items": {
            "type": "string",
            "minLength": 1,
            "maxLength": 200,
            "pattern": "[\\-0-9a-zA-Z]+"
          }
        }
      },
      "additionalProperties": false,
      "required": [
        "SubnetIds",
        "SecurityGroupIds"
      ]
    },
    "DataSourceFieldName": {
      "type": "string",
      "minLength": 1,
      "maxLength": 100
    },
    "DataSourceToIndexFieldMapping": {
      "type": "object",
      "properties": {
        "DataSourceFieldName": {
          "$ref": "#/definitions/DataSourceFieldName"
        },
        "DateFieldFormat": {
          "$ref": "#/definitions/DateFieldFormat"
        },
        "IndexFieldName": {
          "$ref": "#/definitions/IndexFieldName"
        }
      },
      "additionalProperties": false,
      "required": [
        "DataSourceFieldName",
        "IndexFieldName"
      ]
    },
    "DateFieldFormat": {
      "type": "string",
      "minLength": 4,
      "maxLength": 40
    },
    "IndexFieldName": {
      "type": "string",
      "minLength": 1,
      "maxLength": 30
    },
    "DataSourceToIndexFieldMappingList": {
      "type": "array",
      "maxItems": 100,
      "items": {
        "$ref": "#/definitions/DataSourceToIndexFieldMapping"
      }
    },
    "DisableLocalGroups": {
      "type": "boolean"
    },
    "SharePointConfiguration": {
      "description": "SharePoint configuration",
      "type": "object",
      "properties": {
        "SharePointVersion": {
          "type": "string",
          "enum": [
            "SHAREPOINT_ONLINE",
            "SHAREPOINT_2013",
            "SHAREPOINT_2016"
          ]
        },
        "Urls": {
          "type": "array",
          "maxItems": 100,
          "items": {
            "$ref": "#/definitions/Url"
          }
        },
        "SecretArn": {
          "$ref": "#/definitions/SecretArn"
        },
        "CrawlAttachments": {
          "type": "boolean"
        },
        "UseChangeLog": {
          "type": "boolean"
        },
        "InclusionPatterns": {
          "$ref": "#/definitions/DataSourceInclusionsExclusionsStrings"
        },
        "ExclusionPatterns": {
          "$ref": "#/definitions/DataSourceInclusionsExclusionsStrings"
        },
        "VpcConfiguration": {
          "$ref": "#/definitions/DataSourceVpcConfiguration"
        },
        "FieldMappings": {
          "$ref": "#/definitions/DataSourceToIndexFieldMappingList"
        },
        "DocumentTitleFieldName": {
          "$ref": "#/definitions/DataSourceFieldName"
        },
        "DisableLocalGroups": {
          "$ref": "#/definitions/DisableLocalGroups"
        },
        "SslCertificateS3Path": {
          "$ref": "#/definitions/S3Path"
        }
      },
      "additionalProperties": false,
      "required": [
        "Urls",
        "SecretArn",
        "SharePointVersion"
      ]
    },
    "SalesforceConfiguration": {
      "type": "object",
      "properties": {
        "ServerUrl": {
          "$ref": "#/definitions/Url"
        },
        "SecretArn": {
          "$ref": "#/definitions/SecretArn"
        },
        "StandardObjectConfigurations": {
          "$ref": "#/definitions/SalesforceStandardObjectConfigurationList"
        },
        "KnowledgeArticleConfiguration": {
          "$ref": "#/definitions/SalesforceKnowledgeArticleConfiguration"
        },
        "ChatterFeedConfiguration": {
          "$ref": "#/definitions/SalesforceChatterFeedConfiguration"
        },
        "CrawlAttachments": {
          "type": "boolean"
        },
        "StandardObjectAttachmentConfiguration": {
          "$ref": "#/definitions/SalesforceStandardObjectAttachmentConfiguration"
        },
        "IncludeAttachmentFilePatterns": {
          "$ref": "#/definitions/DataSourceInclusionsExclusionsStrings"
        },
        "ExcludeAttachmentFilePatterns": {
          "$ref": "#/definitions/DataSourceInclusionsExclusionsStrings"
        }
      },
      "additionalProperties": false,
      "required": [
        "ServerUrl",
        "SecretArn"
      ]
    },
    "SalesforceStandardObjectConfigurationList": {
      "type": "array",
      "minItems": 1,
      "maxItems": 17,
      "items": {
        "$ref": "#/definitions/SalesforceStandardObjectConfiguration"
      }
    },
    "SalesforceStandardObjectConfiguration": {
      "type": "object",
      "properties": {
        "Name": {
          "$ref": "#/definitions/SalesforceStandardObjectName"
        },
        "DocumentDataFieldName": {
          "$ref": "#/definitions/DataSourceFieldName"
        },
        "DocumentTitleFieldName": {
          "$ref": "#/definitions/DataSourceFieldName"
        },
        "FieldMappings": {
          "$ref": "#/definitions/DataSourceToIndexFieldMappingList"
        }
      },
      "additionalProperties": false,
      "required": [
        "Name",
        "DocumentDataFieldName"
      ]
    },
    "SalesforceStandardObjectName": {
      "type": "string",
      "enum": [
        "ACCOUNT",
        "CAMPAIGN",
        "CASE",
        "CONTACT",
        "CONTRACT",
        "DOCUMENT",
        "GROUP",
        "IDEA",
        "LEAD",
        "OPPORTUNITY",
        "PARTNER",
        "PRICEBOOK",
        "PRODUCT",
        "PROFILE",
        "SOLUTION",
        "TASK",
        "USER"
      ]
    },
    "SalesforceKnowledgeArticleConfiguration": {
      "type": "object",
      "properties": {
        "IncludedStates": {
          "$ref": "#/definitions/SalesforceKnowledgeArticleStateList"
        },
        "StandardKnowledgeArticleTypeConfiguration": {
          "$ref": "#/definitions/SalesforceStandardKnowledgeArticleTypeConfiguration"
        },
        "CustomKnowledgeArticleTypeConfigurations": {
          "$ref": "#/definitions/SalesforceCustomKnowledgeArticleTypeConfigurationList"
        }
      },
      "additionalProperties": false,
      "required": [
        "IncludedStates"
      ]
    },
    "SalesforceKnowledgeArticleStateList": {
      "type": "array",
      "minItems": 1,
      "maxItems": 3,
      "items": {
        "$ref": "#/definitions/SalesforceKnowledgeArticleState"
      }
    },
    "SalesforceKnowledgeArticleState": {
      "type": "string",
      "enum": [
        "DRAFT",
        "PUBLISHED",
        "ARCHIVED"
      ]
    },
    "SalesforceStandardKnowledgeArticleTypeConfiguration": {
      "type": "object",
      "properties": {
        "DocumentDataFieldName": {
          "$ref": "#/definitions/DataSourceFieldName"
        },
        "DocumentTitleFieldName": {
          "$ref": "#/definitions/DataSourceFieldName"
        },
        "FieldMappings": {
          "$ref": "#/definitions/DataSourceToIndexFieldMappingList"
        }
      },
      "additionalProperties": false,
      "required": [
        "DocumentDataFieldName"
      ]
    },
    "SalesforceCustomKnowledgeArticleTypeConfigurationList": {
      "type": "array",
      "minItems": 1,
      "maxItems": 10,
      "items": {
        "$ref": "#/definitions/SalesforceCustomKnowledgeArticleTypeConfiguration"
      }
    },
    "SalesforceCustomKnowledgeArticleTypeConfiguration": {
      "type": "object",
      "properties": {
        "Name": {
          "$ref": "#/definitions/SalesforceCustomKnowledgeArticleTypeName"
        },
        "DocumentDataFieldName": {
          "$ref": "#/definitions/DataSourceFieldName"
        },
        "DocumentTitleFieldName": {
          "$ref": "#/definitions/DataSourceFieldName"
        },
        "FieldMappings": {
          "$ref": "#/definitions/DataSourceToIndexFieldMappingList"
        }
      },
      "additionalProperties": false,
      "required": [
        "Name",
        "DocumentDataFieldName"
      ]
    },
    "SalesforceCustomKnowledgeArticleTypeName": {
      "type": "string",
      "minLength": 1,
      "maxLength": 100
    },
    "SalesforceChatterFeedConfiguration": {
      "type": "object",
      "properties": {
        "DocumentDataFieldName": {
          "$ref": "#/definitions/DataSourceFieldName"
        },
        "DocumentTitleFieldName": {
          "$ref": "#/definitions/DataSourceFieldName"
        },
        "FieldMappings": {
          "$ref": "#/definitions/DataSourceToIndexFieldMappingList"
        },
        "IncludeFilterTypes": {
          "$ref": "#/definitions/SalesforceChatterFeedIncludeFilterTypes"
        }
      },
      "additionalProperties": false,
      "required": [
        "DocumentDataFieldName"
      ]
    },
    "SalesforceChatterFeedIncludeFilterTypes": {
      "type": "array",
      "minItems": 1,
      "maxItems": 2,
      "items": {
        "$ref": "#/definitions/SalesforceChatterFeedIncludeFilterType"
      }
    },
    "SalesforceChatterFeedIncludeFilterType": {
      "type": "string",
      "enum": [
        "ACTIVE_USER",
        "STANDARD_USER"
      ]
    },
    "SalesforceStandardObjectAttachmentConfiguration": {
      "type": "object",
      "properties": {
        "DocumentTitleFieldName": {
          "$ref": "#/definitions/DataSourceFieldName"
        },
        "FieldMappings": {
          "$ref": "#/definitions/DataSourceToIndexFieldMappingList"
        }
      },
      "additionalProperties": false
    },
    "DatabaseConfiguration": {
      "type": "object",
      "properties": {
        "DatabaseEngineType": {
          "$ref": "#/definitions/DatabaseEngineType"
        },
        "ConnectionConfiguration": {
          "$ref": "#/definitions/ConnectionConfiguration"
        },
        "VpcConfiguration": {
          "$ref": "#/definitions/DataSourceVpcConfiguration"
        },
        "ColumnConfiguration": {
          "$ref": "#/definitions/ColumnConfiguration"
        },
        "AclConfiguration": {
          "$ref": "#/definitions/AclConfiguration"
        },
        "SqlConfiguration": {
          "$ref": "#/definitions/SqlConfiguration"
        }
      },
      "additionalProperties": false,
      "required": [
        "ConnectionConfiguration",
        "ColumnConfiguration",
        "DatabaseEngineType"
      ]
    },
    "DatabaseEngineType": {
      "type": "string",
      "enum": [
        "RDS_AURORA_MYSQL",
        "RDS_AURORA_POSTGRESQL",
        "RDS_MYSQL",
        "RDS_POSTGRESQL"
      ]
    },
    "ConnectionConfiguration": {
      "type": "object",
      "properties": {
        "DatabaseHost": {
          "$ref": "#/definitions/DatabaseHost"
        },
        "DatabasePort": {
          "$ref": "#/definitions/DatabasePort"
        },
        "DatabaseName": {
          "$ref": "#/definitions/DatabaseName"
        },
        "TableName": {
          "$ref": "#/definitions/TableName"
        },
        "SecretArn": {
          "$ref": "#/definitions/SecretArn"
        }
      },
      "additionalProperties": false,
      "required": [
        "DatabaseHost",
        "DatabasePort",
        "DatabaseName",
        "TableName",
        "SecretArn"
      ]
    },
    "DatabaseHost": {
      "type": "string",
      "minLength": 1,
      "maxLength": 253
    },
    "DatabasePort": {
      "type": "integer",
      "minimum": 1,
      "maximum": 65535
    },
    "DatabaseName": {
      "type": "string",
      "minLength": 1,
      "maxLength": 100
    },
    "TableName": {
      "type": "string",
      "minLength": 1,
      "maxLength": 100
    },
    "ColumnConfiguration": {
      "type": "object",
      "properties": {
        "DocumentIdColumnName": {
          "$ref": "#/definitions/ColumnName"
        },
        "DocumentDataColumnName": {
          "$ref": "#/definitions/ColumnName"
        },
        "DocumentTitleColumnName": {
          "$ref": "#/definitions/ColumnName"
        },
        "FieldMappings": {
          "$ref": "#/definitions/DataSourceToIndexFieldMappingList"
        },
        "ChangeDetectingColumns": {
          "$ref": "#/definitions/ChangeDetectingColumns"
        }
      },
      "additionalProperties": false,
      "required": [
        "DocumentIdColumnName",
        "DocumentDataColumnName",
        "ChangeDetectingColumns"
      ]
    },
    "ChangeDetectingColumns": {
      "type": "array",
      "minItems": 1,
      "maxItems": 5,
      "items": {
        "$ref": "#/definitions/ColumnName"
      }
    },
    "ColumnName": {
      "type": "string",
      "minLength": 1,
      "maxLength": 100
    },
    "SqlConfiguration": {
      "type": "object",
      "properties": {
        "QueryIdentifiersEnclosingOption": {
          "$ref": "#/definitions/QueryIdentifiersEnclosingOption"
        }
      },
      "additionalProperties": false
    },
    "QueryIdentifiersEnclosingOption": {
      "type": "string",
      "enum": [
        "DOUBLE_QUOTES",
        "NONE"
      ]
    },
    "AclConfiguration": {
      "type": "object",
      "properties": {
        "AllowedGroupsColumnName": {
          "$ref": "#/definitions/ColumnName"
        }
      },
      "additionalProperties": false,
      "required": [
        "AllowedGroupsColumnName"
      ]
    },
    "OneDriveConfiguration": {
      "type": "object",
      "properties": {
        "TenantDomain": {
          "$ref": "#/definitions/TenantDomain"
        },
        "SecretArn": {
          "$ref": "#/definitions/SecretArn"
        },
        "OneDriveUsers": {
          "$ref": "#/definitions/OneDriveUsers"
        },
        "InclusionPatterns": {
          "$ref": "#/definitions/DataSourceInclusionsExclusionsStrings"
        },
        "ExclusionPatterns": {
          "$ref": "#/definitions/DataSourceInclusionsExclusionsStrings"
        },
        "FieldMappings": {
          "$ref": "#/definitions/DataSourceToIndexFieldMappingList"
        },
        "DisableLocalGroups": {
          "$ref": "#/definitions/DisableLocalGroups"
        }
      },
      "additionalProperties": false,
      "required": [
        "TenantDomain",
        "SecretArn",
        "OneDriveUsers"
      ]
    },
    "TenantDomain": {
      "type": "string",
      "minLength": 1,
      "maxLength": 256,
      "pattern": "^([a-zA-Z0-9]+(-[a-zA-Z0-9]+)*\\.)+[a-z]{2,}$"
    },
    "OneDriveUsers": {
      "type": "object",
      "properties": {
        "OneDriveUserList": {
          "$ref": "#/definitions/OneDriveUserList"
        },
        "OneDriveUserS3Path": {
          "$ref": "#/definitions/S3Path"
        }
      },
      "additionalProperties": false,
      "oneOf": [
        {
          "required": [
            "OneDriveUserList"
          ]
        },
        {
          "required": [
            "OneDriveUserS3Path"
          ]
        }
      ]
    },
    "OneDriveUserList": {
      "type": "array",
      "minItems": 1,
      "maxItems": 100,
      "items": {
        "$ref": "#/definitions/OneDriveUser"
      }
    },
    "OneDriveUser": {
      "type": "string",
      "minLength": 1,
      "maxLength": 256,
      "pattern": "^(?!\\s).+@([a-zA-Z0-9_\\-\\.]+)\\.([a-zA-Z]{2,5})$"
    },
    "ServiceNowConfiguration": {
      "type": "object",
      "properties": {
        "HostUrl": {
          "$ref": "#/definitions/ServiceNowHostUrl"
        },
        "SecretArn": {
          "$ref": "#/definitions/SecretArn"
        },
        "ServiceNowBuildVersion": {
          "$ref": "#/definitions/ServiceNowBuildVersionType"
        },
        "AuthenticationType": {
          "$ref": "#/definitions/ServiceNowAuthenticationType"
        },
        "KnowledgeArticleConfiguration": {
          "$ref": "#/definitions/ServiceNowKnowledgeArticleConfiguration"
        },
        "ServiceCatalogConfiguration": {
          "$ref": "#/definitions/ServiceNowServiceCatalogConfiguration"
        }
      },
      "additionalProperties": false,
      "required": [
        "HostUrl",
        "SecretArn",
        "ServiceNowBuildVersion"
      ]
    },
    "ServiceNowBuildVersionType": {
      "type": "string",
      "enum": [
        "LONDON",
        "OTHERS"
      ]
    },
    "ServiceNowAuthenticationType": {
      "type": "string",
      "enum": [
        "HTTP_BASIC",
        "OAUTH2"
      ]
    },
    "ServiceNowServiceCatalogConfiguration": {
      "type": "object",
      "properties": {
        "CrawlAttachments": {
          "type": "boolean"
        },
        "IncludeAttachmentFilePatterns": {
          "$ref": "#/definitions/DataSourceInclusionsExclusionsStrings"
        },
        "ExcludeAttachmentFilePatterns": {
          "$ref": "#/definitions/DataSourceInclusionsExclusionsStrings"
        },
        "DocumentDataFieldName": {
          "$ref": "#/definitions/DataSourceFieldName"
        },
        "DocumentTitleFieldName": {
          "$ref": "#/definitions/DataSourceFieldName"
        },
        "FieldMappings": {
          "$ref": "#/definitions/DataSourceToIndexFieldMappingList"
        }
      },
      "additionalProperties": false,
      "required": [
        "DocumentDataFieldName"
      ]
    },
    "ServiceNowHostUrl": {
      "type": "string",
      "minLength": 1,
      "maxLength": 2048,
      "pattern": "^(?!(^(https?|ftp|file):\\/\\/))[a-z0-9-]+(\\.service-now\\.com)$"
    },
    "ServiceNowKnowledgeArticleConfiguration": {
      "type": "object",
      "properties": {
        "CrawlAttachments": {
          "type": "boolean"
        },
        "IncludeAttachmentFilePatterns": {
          "$ref": "#/definitions/DataSourceInclusionsExclusionsStrings"
        },
        "ExcludeAttachmentFilePatterns": {
          "$ref": "#/definitions/DataSourceInclusionsExclusionsStrings"
        },
        "DocumentDataFieldName": {
          "$ref": "#/definitions/DataSourceFieldName"
        },
        "DocumentTitleFieldName": {
          "$ref": "#/definitions/DataSourceFieldName"
        },
        "FieldMappings": {
          "$ref": "#/definitions/DataSourceToIndexFieldMappingList"
        },
        "FilterQuery": {
          "$ref": "#/definitions/ServiceNowKnowledgeArticleFilterQuery"
        }
      },
      "additionalProperties": false,
      "required": [
        "DocumentDataFieldName"
      ]
    },
    "ServiceNowKnowledgeArticleFilterQuery": {
      "type": "string",
      "minLength": 1,
      "maxLength": 2048
    },
    "ConfluenceConfiguration": {
      "type": "object",
      "properties": {
        "ServerUrl": {
          "$ref": "#/definitions/Url"
        },
        "SecretArn": {
          "$ref": "#/definitions/SecretArn"
        },
        "Version": {
          "$ref": "#/definitions/ConfluenceVersion"
        },
        "SpaceConfiguration": {
          "$ref": "#/definitions/ConfluenceSpaceConfiguration"
        },
        "PageConfiguration": {
          "$ref": "#/definitions/ConfluencePageConfiguration"
        },
        "BlogConfiguration": {
          "$ref": "#/definitions/ConfluenceBlogConfiguration"
        },
        "AttachmentConfiguration": {
          "$ref": "#/definitions/ConfluenceAttachmentConfiguration"
        },
        "VpcConfiguration": {
          "$ref": "#/definitions/DataSourceVpcConfiguration"
        },
        "InclusionPatterns": {
          "$ref": "#/definitions/DataSourceInclusionsExclusionsStrings"
        },
        "ExclusionPatterns": {
          "$ref": "#/definitions/DataSourceInclusionsExclusionsStrings"
        }
      },
      "additionalProperties": false,
      "required": [
        "ServerUrl",
        "SecretArn",
        "Version"
      ]
    },
    "ConfluenceVersion": {
      "type": "string",
      "enum": [
        "CLOUD",
        "SERVER"
      ]
    },
    "ConfluenceSpaceConfiguration": {
      "type": "object",
      "properties": {
        "CrawlPersonalSpaces": {
          "type": "boolean"
        },
        "CrawlArchivedSpaces": {
          "type": "boolean"
        },
        "IncludeSpaces": {
          "$ref": "#/definitions/ConfluenceSpaceList"
        },
        "ExcludeSpaces": {
          "$ref": "#/definitions/ConfluenceSpaceList"
        },
        "SpaceFieldMappings": {
          "$ref": "#/definitions/ConfluenceSpaceFieldMappingsList"
        }
      },
      "additionalProperties": false
    },
    "ConfluenceSpaceFieldMappingsList": {
      "type": "array",
      "minItems": 1,
      "maxItems": 4,
      "items": {
        "$ref": "#/definitions/ConfluenceSpaceToIndexFieldMapping"
      }
    },
    "ConfluenceSpaceToIndexFieldMapping": {
      "type": "object",
      "properties": {
        "DataSourceFieldName": {
          "$ref": "#/definitions/ConfluenceSpaceFieldName"
        },
        "DateFieldFormat": {
          "$ref": "#/definitions/DateFieldFormat"
        },
        "IndexFieldName": {
          "$ref": "#/definitions/IndexFieldName"
        }
      },
      "additionalProperties": false,
      "required": [
        "DataSourceFieldName",
        "IndexFieldName"
      ]
    },
