Schema:
{
    "typeName": "AWS::WorkSpacesWeb::UserSettings",
    "description": "Definition of AWS::WorkSpacesWeb::UserSettings Resource Type",
    "definitions": {
        "CookieSpecification": {
            "type": "object",
            "properties": {
                "Domain": {
                    "type": "string",
                    "maxLength": 253,
                    "minLength": 0,
                    "pattern": "^(\\.?)(?:[a-z0-9](?:[a-z0-9-]{0,61}[a-z0-9])?\\.)*[a-z0-9][a-z0-9-]{0,61}[a-z0-9]$"
                },
                "Name": {
                    "type": "string",
                    "maxLength": 4096,
                    "minLength": 0
                },
                "Path": {
                    "type": "string",
                    "maxLength": 2000,
                    "minLength": 0,
                    "pattern": "^/(\\S)*$"
                }
            },
            "required": [
                "Domain"
            ],
            "additionalProperties": false
        },
        "CookieSynchronizationConfiguration": {
            "type": "object",
            "properties": {
                "Allowlist": {
                    "type": "array",
                    "items": {
                        "$ref": "#/definitions/CookieSpecification"
                    },
                    "maxItems": 10,
                    "minItems": 0,
                    "insertionOrder": false
                },
                "Blocklist": {
                    "type": "array",
                    "items": {
                        "$ref": "#/definitions/CookieSpecification"
                    },
                    "maxItems": 10,
                    "minItems": 0,
                    "insertionOrder": false
                }
            },
            "required": [
                "Allowlist"
            ],
            "additionalProperties": false
        },
        "EnabledType": {
            "type": "string",
            "enum": [
                "Disabled",
                "Enabled"
            ]
        },
        "EncryptionContextMap": {
            "type": "object",
            "patternProperties": {
                "^[\\s\\S]*$": {
                    "type": "string",
                    "maxLength": 131072,
                    "minLength": 0,
                    "pattern": "^[\\s\\S]*$"
                }
            },
            "additionalProperties": false
        },
        "Tag": {
            "type": "object",
            "properties": {
                "Key": {
                    "type": "string",
                    "maxLength": 128,
                    "minLength": 1,
                    "pattern": "^([\\p{L}\\p{Z}\\p{N}_.:/=+\\-@]*)$"
                },
                "Value": {
                    "type": "string",
                    "maxLength": 256,
                    "minLength": 0,
                    "pattern": "^([\\p{L}\\p{Z}\\p{N}_.:/=+\\-@]*)$"
                }
            },
            "required": [
                "Key",
                "Value"
            ],
            "additionalProperties": false
        }
    },
    "properties": {
        "AdditionalEncryptionContext": {
            "$ref": "#/definitions/EncryptionContextMap"
        },
        "AssociatedPortalArns": {
            "type": "array",
            "items": {
                "type": "string",
                "maxLength": 2048,
                "minLength": 20,
                "pattern": "^arn:[\\w+=\\/,.@-]+:[a-zA-Z0-9\\-]+:[a-zA-Z0-9\\-]*:[a-zA-Z0-9]{1,12}:[a-zA-Z]+(\\/[a-fA-F0-9\\-]{36})+$"
            },
            "insertionOrder": false
        },
        "CookieSynchronizationConfiguration": {
            "$ref": "#/definitions/CookieSynchronizationConfiguration"
        },
        "CopyAllowed": {
            "$ref": "#/definitions/EnabledType"
        },
        "CustomerManagedKey": {
            "type": "string",
            "maxLength": 2048,
            "minLength": 20,
            "pattern": "^arn:[\\w+=\\/,.@-]+:kms:[a-zA-Z0-9\\-]*:[a-zA-Z0-9]{1,12}:key\\/[a-zA-Z0-9-]+$"
        },
        "DisconnectTimeoutInMinutes": {
            "type": "number",
            "default": null,
            "maximum": 600,
            "minimum": 1
        },
        "DownloadAllowed": {
            "$ref": "#/definitions/EnabledType"
        },
        "IdleDisconnectTimeoutInMinutes": {
            "type": "number",
            "default": null,
            "maximum": 60,
            "minimum": 0
        },
        "PasteAllowed": {
            "$ref": "#/definitions/EnabledType"
        },
        "PrintAllowed": {
            "$ref": "#/definitions/EnabledType"
        },
        "Tags": {
            "type": "array",
            "items": {
                "$ref": "#/definitions/Tag"
            },
            "maxItems": 200,
            "minItems": 0,
            "insertionOrder": false
        },
        "UploadAllowed": {
            "$ref": "#/definitions/EnabledType"
        },
        "UserSettingsArn": {
            "type": "string",
            "maxLength": 2048,
            "minLength": 20,
            "pattern": "^arn:[\\w+=\\/,.@-]+:[a-zA-Z0-9\\-]+:[a-zA-Z0-9\\-]*:[a-zA-Z0-9]{1,12}:[a-zA-Z]+(\\/[a-fA-F0-9\\-]{36})+$"
        }
    },
    "required": [
        "CopyAllowed",
        "DownloadAllowed",
        "PasteAllowed",
        "PrintAllowed",
        "UploadAllowed"
    ],
    "readOnlyProperties": [
        "/properties/AssociatedPortalArns",
        "/properties/UserSettingsArn"
    ],
    "writeOnlyProperties": [
        "/properties/AdditionalEncryptionContext",
        "/properties/CustomerManagedKey"
    ],
    "createOnlyProperties": [
        "/properties/AdditionalEncryptionContext",
        "/properties/CustomerManagedKey"
    ],
    "primaryIdentifier": [
        "/properties/UserSettingsArn"
    ],
    "handlers": {
        "create": {
            "permissions": [
                "workspaces-web:CreateUserSettings",
                "workspaces-web:GetUserSettings",
                "workspaces-web:ListTagsForResource",
                "workspaces-web:TagResource",
                "kms:CreateGrant",
