                "workspaces-web:TagResource",
                "workspaces-web:UntagResource",
                "workspaces-web:GetUserSettings",
                "workspaces-web:ListTagsForResource",
                "kms:CreateGrant",
                "kms:DescribeKey",
                "kms:GenerateDataKey",
                "kms:Decrypt"
            ]
        },
        "delete": {
            "permissions": [
