                "workspaces-web:DeleteUserSettings",
                "kms:CreateGrant",
                "kms:DescribeKey",
                "kms:GenerateDataKey",
                "kms:Decrypt"
            ]
        },
        "list": {
            "permissions": [
                "workspaces-web:ListUserSettings",
                "kms:CreateGrant",
                "kms:DescribeKey",
                "kms:GenerateDataKey",
                "kms:Decrypt"
            ]
        }
    },
    "tagging": {
        "cloudFormationSystemTags": false,
        "tagOnCreate": true,
        "tagProperty": "/properties/Tags",
        "tagUpdatable": true,
        "taggable": true
    },
    "additionalProperties": false
}
